import "../HomePage/HomePage.scss"
import React from "react";
import SelectedVideo from "../../components/SelectedVideo/SelectedVideo";
import Article from "../../components/Article/Article";
import Comment from "../../components/Comment/Comment";
import SideVideos from "../../components/SideVideos/SideVideos";
import { useEffect} from "react";
import { useParams } from 'react-router-dom';
import axios from "axios";

const HomePage = ({ setSelectedVideoInfo, 
                    selectedVideoInfo,
                    videosInfo, 
                    sideVideos, 
                    handleVideoSelect, 
                    dateAndTimeOfComment}) => {

const {videoId} = useParams();
const brainFlixApiKey = "2515aa87-f829-40de-ade0-d0166853f149";

// useEffect(()=>{
//   handleVideoSelect(videoId);
// }, [videoId, handleVideoSelect]);

useEffect(() => {
  const fetchVideoDetails = async (id) => {
    const url = `https://unit-3-project-api-0a5620414506.herokuapp.com/videos/${id}?api_key=${brainFlixApiKey}`;
    try {
      const response = await axios.get(url);
      setSelectedVideoInfo(response.data);
    } catch (error) {
      console.error("Could not fetch video details!", error);
    }
  };

  if (videoId) {
    fetchVideoDetails(videoId);
  } else if (videosInfo.length > 0) {
    // Default to the first video's details if no videoId is specified
    fetchVideoDetails(videosInfo[0].id);
  }
  // This effect depends on videoId and videosInfo. When videosInfo changes (e.g., after being fetched),
  // it will attempt to load the first video's details if no specific videoId is provided.
}, [videoId, videosInfo, setSelectedVideoInfo]);

  return (
    <div>
      <SelectedVideo selectedVideoInfo={selectedVideoInfo} />
      <div className="aside__videos">
        <div className="aside__comment">
          <Article selectedVideoInfo={selectedVideoInfo} 
             dateAndTimeOfComment={dateAndTimeOfComment}/>
          <Comment selectedVideoInfo={selectedVideoInfo}
             dateAndTimeOfComment={dateAndTimeOfComment} />
        </div>
        <div className="aside__videosOnly">
          <SideVideos
            sideVideos={sideVideos}
            handleVideoSelect={handleVideoSelect}
          />
        </div>
      </div>
    </div>
  );
};
export default HomePage;
