// App.jsx
import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./App.scss";
import Header from "./components/Header/Header";
import HomePage from "./pages/HomePage/HomePage";
import Uploads from "./pages/Uploads/Uploads";
import axios from "axios";

const { REACT_APP_BACKEND_URL } = process.env;
console.log("Testing url",REACT_APP_BACKEND_URL)

const App = () => {

  const [selectedVideoInfo, setSelectedVideoInfo] = useState({});
  const [videosInfo, setVideosInfo] = useState([]);
  useEffect(() => {
    const defaultVideoId = videosInfo.length > 0? videosInfo[0].id: null;
    const fetchVideosAll = async () => {
      try {
        const { data } = await axios.get(REACT_APP_BACKEND_URL);
        setVideosInfo(data);
      } catch (error) {
        console.error("Could not fetch videos!", error);
      }
    };
    fetchVideosAll(defaultVideoId);
  }, []);

  return (
    <BrowserRouter>
      <div className="App">
        <Header /> {/* Renders the Header component */}
        <Routes>
          {/* Route for the homepage */}
          <Route
            path="/"
            element={
              <HomePage
                selectedVideoInfo={selectedVideoInfo}
                setSelectedVideoInfo={setSelectedVideoInfo}
                videosInfo={videosInfo}
              />
            }
          ></Route>
          {/* Route for a specific video by its ID */}
          <Route
            path="/:videoId"
            element={
              <HomePage
                selectedVideoInfo={selectedVideoInfo}
                setSelectedVideoInfo={setSelectedVideoInfo}
                videosInfo={videosInfo}
              />
            }
          ></Route>
          {/* Route for the upload page */}
          <Route path="/uploads" element={<Uploads />}></Route>
        </Routes>
      </div>
    </BrowserRouter>
  );
};
export default App;
